	try
	{
		IOperand const *res = Operand<char>("-128", Int8) + Operand<char>("1", Int8);
		std::cout << " ✅";
		grade++;
		delete res;
	}
	catch (std::exception &e)
	{
		std::cout << " ❌";
		ss << std::endl << "1) -128 + 1 = -127 (you: " << e.what() << ")";
	}

