#include <iostream>
#include <string>

int main(void)
{
	try
	{
		std::cout << stoi(std::string("999")) << std::endl;
		std::cout << atoi("999") << std::endl;

		std::cout << stoi(std::string("99999999")) << std::endl;
		std::cout << stod(std::string(".0")) << std::endl;
	}
	catch (std::exception &e)
	{
		std::cout << e.what() << std::endl;
	}
	return (0);
}

